import Layout from '../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import Button from '@material-ui/core/Button';
import TestHeader from '../components/TestHeader';
import NeonLogo from '../components/NeonLogo';
import * as Scroll from 'react-scroll';
import Carousel from '../components/Carousel';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import SpotifyPlayer from 'react-spotify-player';

import {
  Link,
  Element,
  Events,
  animateScroll as scroll,
  scrollSpy,
  scroller,
} from 'react-scroll';

const size = {
  width: '90%',
  height: 650,
};
const view = 'list'; // or 'coverart'
const theme = 'black'; // or 'white'

const Index = props => (
  <Layout>
    <header>
      <div id='text' className='scrollBox'></div>
      <div className='gelkLogo'>
        <img
          className='gelkImageLogo'
          src='/testNeon.gif'
          alt='good enough live karaoke neon logo'
        />
      </div>
      <div className='songButton'>
        <a href='/list'>View Song List</a>
      </div>
    </header>
    <div className='chevron'>
      <Link
        className='scrollChevron'
        to='text'
        spy={true}
        smooth={true}
        duration={500}
      >
        <svg
          height='40px'
          width='40px'
          fill='#F1F1F1'
          xmlns='http://www.w3.org/2000/svg'
          viewBox='0 0 24 24'
          x='0px'
          y='0px'
        >
          <path
            fill-rule='evenodd'
            d='M12,23 C5.92486775,23 1,18.0751322 1,12 C1,5.92486775 5.92486775,1 12,1 C18.0751322,1 23,5.92486775 23,12 C23,18.0751322 18.0751322,23 12,23 Z M12,21 C16.9705627,21 21,16.9705627 21,12 C21,7.02943725 16.9705627,3 12,3 C7.02943725,3 3,7.02943725 3,12 C3,16.9705627 7.02943725,21 12,21 Z M15.2928932,9.29289322 L16.7071068,10.7071068 L12,15.4142136 L7.29289322,10.7071068 L8.70710678,9.29289322 L12,12.5857864 L15.2928932,9.29289322 Z'
          ></path>
        </svg>
      </Link>
    </div>
    <style jsx>{`

    .songButton {
      width: 100%;
      text-align: center;
      margin-bottom: 4%;
    }

    .songButton a {
      background: none;
      padding: 10px 20px;
      border-radius: 50px;
      border: 3px solid #f1f1f1;
      text-align:center;
      transition: all 0.4s;
      color: #f1f1f1;
      text-decoration: none;
      font-size: 20px;
      cursor: pointer
    }

    .songButton:hover a {
      text-shadow: 0 -40px 100px, 0 0 2px, 0 0 1em #761f6b, 0 0 0.5em #761f6b,
          0 0 0.1em #761f6b, 0 10px 3px #000;
    }



    .songButton a:hover {
      opacity: 1;
       text-shadow: 0 -40px 100px, 0 0 2px, 0 0 1em #761f6b, 0 0 0.5em #761f6b,
          0 0 0.1em #761f6b, 0 10px 3px #000;
    }

    .gelkImageLogo {
      display: block;
      width: 100%;
      height: auto;
      max-height: 800px;
    }

    .gelkLogo {
      width: 70%;
      margin: 0 auto;
      margin-top: 3%;
      max-width: 1400px;
      margin-bottom: 2.5%;
    }
      svg:hover {
        fill: rgba(241,241,241, 0.6);
        cursor: pointer;
      }

      svg {
        transition: all 0.4s;
      }
      h1,
      a {
        font-family: 'Montserrat', sans-serif;
      }

      ul {
        padding: 0;
      }

      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
      }

      a:hover {
        opacity: 0.6;
      }

      .chevron {
        margin-bottom: 200px;
        display: flex;
        width: 94%;
        justify-content: flex-end;
        position: fixed;
        top: 88vh;
        transform: scale(1, -1)
      }

      .wrapper {
        margin: 0 auto;
        max-width: 960px;
        padding: 0 2%;
      }

      .scrollBox{
        position: relative;
        color: white;
        width: 20px;
        height: 20px;
        bottom: 200px;
      }
      }
    `}</style>
    <>
      {/* <Carousel /> */}
      <div className='spotifyAbout'>
        <div className='flexOneSide'>
          <div className='introText'>
            <h3>About Us</h3>
            <p>
              Good Enough Live Karaoke is Toronto's most popular live band
              karaoke experience, letting anyone with the desire and guts to
              join us on stage, take the mic and sing! Ideal for corporate
              events, birthday parties and weddings, Good Enough is a live band
              featuring guitars, bass, keyboards and drums, and a repertoire of
              over 400 songs spanning genres and eras. Get in touch at{' '}
              <a
                href='mailto:sing@goodenoughlivekaraoke.com?Subject=Hello%20'
                target='_top'
              >
                sing@goodenoughlivekaraoke.com
              </a>{' '}
              to book your event, and follow us on Instagram to see weekly
              public shows across Ontario. Since 2013, we have played thousands
              of hours on stage at private and public events with our unique,
              interactive rock'n'roll experience. It’s a bit terrifying, hugely
              exhilarating and completely awesome!
            </p>
          </div>
        </div>
        <div className='flexTwoSide'>
          <div className='spotify'>
            <SpotifyPlayer
              uri='spotify:playlist:4QmX44dyzZa6kObYm5jXzS'
              size={size}
              view={view}
              theme={theme}
            />
          </div>
        </div>
      </div>
    </>
    <style jsx>{`
      @import url('https://fonts.googleapis.com/css?family=Concert+One|Montserrat&display=swap');

      h3 {
        text-align:center;
        font-family: 'Concert One', sans-serif;
        color: #e53441
      }

      .spotify {
        margin: 0 auto;
        display: flex;
        justify-content: center;
        margin: 2% 0;
      }

      .spotifyAbout {
        display: flex;
        width: 100%;
      }

      .flexOneSide {
        width: 60%;
        padding: 2%;
        background: #f1f1f1;
      }

      .flexTwoSide {
        background: #e53441;
        width: 40%;
        padding: 2%;
      }

      .introText {
        max-width: 1400px;
        font-family: 'Montserrat', sans-serif;
        font-size: 28px;
        color: rgb(25,3,22)
      }

      .introText a {
        color: rgb(25,3,22) !important;
      }

      .introText a:hover {
        color: #e53441 !important;
      }


      a {
        color: rgb(25,3,22)
        text-decoration: underline;
        cursor: pointer;
        transition: all 0.4s;
      }

      a:hover {
        opacity: 0.65;
      }

      .introText p {
        line-height: 38px;
        margin-bottom: 50px;
        text-align: left;
      }

      h2 {
        text-align: center;
        font-size: 70px;
        margin-top: -100px;
        font-family: 'Concert One', cursive;
        color: #f1f1f1;
      }

      @media screen and (max-width: 1075px) {
        .introText h2 {
          font-size: 30px;
          font-family: 'Concert One', cursive;
        }

        .introText {
          font-size: 20px;
        }

        .introText p {
          line-height: 36px;
        }

        .gelkLogo {
          margin-top: 33%;
        }
      }

      @media screen and (max-width: 690px) {
        .gelkLogo {
          margin-top: 25vh;
        }

        .chevron {
          margin-top: 50px;
        }
        .introText h2 {
          font-size: 30px;
          margin-top: -70px;
          margin-bottom: 30px;
        }

        .introText {
          font-size: 16px;
        }

        .introText p {
          line-height: 32px;
        }
      }

      @media screen and (max-width: 557px) {
        svg {
          width: 30px;
        }

        .introText h2 {
          font-size: 22px;
        }

        .gelkLogo {
          width: 90%;
        }
        .gelkLogo {
          margin-top: 33vh;
        }
      }
    `}</style>
  </Layout>
);

export default Index;
