import React from 'react';
import ReactDOM from 'react-dom';
const contentful = require('contentful');

import Slider from 'react-slick';
import '../index.css';

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      about: [],
    };
  }
  componentDidMount() {
    const client = contentful.createClient({
      // This is the space ID. A space is like a project folder in Contentful terms
      space: 'sqmp3jmwaedr',
      // This is the access token for this space. Normally you get both ID and the token in the Contentful web app
      accessToken: '01TsFxZR2mrw_VWsuCtzZCBCzKsrvCTDX9is-6UPzqU',
    });
    // content type relates to the content type name in contentful
    // figured it outttt
    // 'post' instead of songList, post is linked to author so when post response you get author object as well
    client.getEntries({ content_type: 'about' }).then(response => {
      this.setState({
        about: response.items,
        photoOne: response.items[0].fields.photos[0].fields.file.url,
        photoTwo: response.items[0].fields.photos[1].fields.file.url,
        photoThree: response.items[0].fields.photos[2].fields.file.url,
        photoFour: response.items[0].fields.photos[3].fields.file.url,
        testimonialOne: {
          paragraphOne:
            response.items[0].fields.testimonialOne.content[0].content[0].value,
          paragraphTwo:
            response.items[0].fields.testimonialOne.content[1].content[0].value,
          name:
            response.items[0].fields.testimonialOne.content[2].content[0].value,
        },
        testimonialTwo: {
          paragraphOne:
            response.items[0].fields.testimonialTwo.content[0].content[0].value,
          paragraphTwo:
            response.items[0].fields.testimonialTwo.content[1].content[0].value,
          name:
            response.items[0].fields.testimonialTwo.content[2].content[0].value,
        },
        testimonialThree: {
          paragraphOne:
            response.items[0].fields.testimonialThree.content[0].content[0]
              .value,
          paragraphTwo:
            response.items[0].fields.testimonialThree.content[1].content[0]
              .value,
          name:
            response.items[0].fields.testimonialThree.content[2].content[0]
              .value,
        },
        testimonialFour: {
          paragraphOne:
            response.items[0].fields.testimonialFour.content[0].content[0]
              .value,
          paragraphTwo:
            response.items[0].fields.testimonialFour.content[1].content[0]
              .value,
          name:
            response.items[0].fields.testimonialFour.content[2].content[0]
              .value,
        },
      });
    });
  }
  render() {
    var settings = {
      dots: true,
      autoplay: true,
      autoplaySpeed: 3000,
    };
    return (
      <div className='container'>
        <Slider {...settings}>
          <div>
            <img src={this.state.photoOne} />
          </div>
          <div>
            <p>{this.state.testimonialOne.paragraphOne}</p>
            <p>{this.state.testimonialOne.paragraphTwo}</p>
            <p>
              <span>{this.state.testimonailOne.name}</span>
            </p>
          </div>
          <div>
            <img src={this.state.photoTwo} />
          </div>
          <div>
            <p>
              'Good Enough'; is a blatant misnomer - the band was excellent and
              all of my guests gushed compliments.
            </p>
            <p>
              Best of all, the band was very encouraging and forgiving, which
              led to an incredible breadth of participation from people not
              expected to sing.
            </p>
            <p>
              <span>Malcolm White, celebrated 50th Birthday</span>
            </p>
          </div>
          <div>
            <img src={this.state.photoFour} />
          </div>
          <div>
            <p>We couldn't have asked for a better group of people.</p>
            <p>
              The band learned two songs of our choosing for the big day and the
              father of the bride even got to sit on drums for a song.
            </p>
            <p>
              All of our guests had a ton of fun and I cannot recommend Good
              Enough Live Karaoke enough!
            </p>
            <p>
              <span>Sean O'Grady</span>
            </p>
          </div>
          <div>
            <img src={this.state.photoThree} />
          </div>
          <div>
            <p>
              Thank you for you outstanding service and special thanks for an
              amazing, talented band.
            </p>
            <p>I hope to work with you sometime in the future again!</p>
            <p>
              <span>Katerina Semenova, Premise LED</span>
            </p>
          </div>
          {/*  */}
        </Slider>
      </div>
    );
  }
}

export default Carousel;
