      }
    });

    return (
      <Layout>
        <div className='listWrapper'>
          <h1 className='contactNeon'>UPCOMING SHOWS</h1>
          <h4>click a show for more info / tickets</h4>
          <div className='shows'>
            {/* {shows} */}
            {/* {this.state.shows.length === 0 ? (
              <ColorCircularProgress size={100} thickness={5} />
            ) : (
              shows
            )} */}
          </div>
        </div>
        <style jsx>{`
          h1 {
            white-space: nowrap;
            margin: 0 -5px 0 0;
            letter-spacing: 5px;
            line-height: 1;
            font-size: 7vw;
            font-family: 'Roboto', sans-serif;
            font-weight: 100;
            text-align: center;
            color: #f1f1f1;
          }

          h4 {
            font-family: 'Roboto', sans-serif;
            font-weight: 100;
            text-align: center;
            font-size: 20px;
            color: #f1f1f1;
          }

          .contactNeon {
            margin-bottom: 70px;
          }

          .listWrapper {
            margin-top: 25vh;
          }

          .shows {
            font-family: 'Roboto', sans-serif;
            margin: 0 auto;
            text-align: center;
            font-size: 26px;
            color: #f1f1f1;
            min-height: 55vh;
            width: 65%;
          }

          p {
            color: #f1f1f1;
            font-size: 20px;
            display: flex;
            justify-content: center;
          }

          a:hover {
            text-decoration: underline;
          }
        `}</style>
      </Layout>
    );
  }
}
