import Layout from '../components/MyLayout';
const contentful = require('contentful');
import ClientImages from '../components/ClientImages';
import TestHeader from '../components/TestHeader';
import CircularProgress from '@material-ui/core/CircularProgress';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import video from '../assets/videosHead.svg';
import photos from '../assets/photosHead.svg';

const ColorCircularProgress = withStyles({
  root: {
    color: '#f1f1f1',
  },
})(CircularProgress);

export default class About extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      media: [],
    };
  }

  componentDidMount() {
    const client = contentful.createClient({
      space: 'sqmp3jmwaedr',
      accessToken: '01TsFxZR2mrw_VWsuCtzZCBCzKsrvCTDX9is-6UPzqU',
    });

    client.getEntries({ content_type: 'picsAndVideos' }).then(response => {
      this.setState({
        media: response.items,
      });
    });
  }

  render() {
    const videos = this.state.media.map((videos, i) => {
      if (videos.fields.videos) {
        return videos.fields.videos.map((videos, i) => {
          return (
            <>
              <div className='pictures'>
                <div className='top'>
                  <video width='400' height='320' controls>
                    <source src={videos.fields.file.url} type='video/mp4' />
                    ]Your browser does not support the video tag.
                  </video>
                </div>
                <div className='bottom'>
                  <p>{videos.fields.title}</p>
                </div>
              </div>

              <style jsx>{`
                p {
                  font-family: 'Roboto', sans-serif;
                  font-size: 24px;
                  font-weight: 700;
                  color: #f1f1f1;
                  text-align: center;
                }

                .pictures {
                  width: 40%;
                  margin: 25px auto;
                  position: relative;
                }

                video {
                  width: 100%;
                  height: auto;
                }

                .bottom {
                  margin-top: -15px;
                }
              `}</style>
            </>
          );
        });
      }
    });

    const pictures = this.state.media.map((pictures, i) => {
      if (pictures.fields.pictures) {
        return pictures.fields.pictures.map((pictures, i) => {
          return (
            <>
              <div className='pictures'>
                <div className='top'>
                  <img
                    src={pictures.fields.file.url}
                    alt={pictures.fields.title}
                  />
                </div>
                <div className='bottom'>
                  <p>{pictures.fields.title}</p>
                </div>
              </div>

              <style jsx>{`
                p {
                  font-family: 'Roboto', sans-serif;
                  font-size: 24px;
                  font-weight: 700;
                  color: #f1f1f1;
                }

                .pictures {
                  width: 30%;
                  margin: 25px auto;
                }

                .pictures .top {
                  max-width: 300px;
                  max-height: 400px;
                }

                img {
                  width: 100%;
                  height: auto;
                }

                .bottom {
                  margin-top: -20px;
                  max-width: 300px;
                  text-align: center;
                }
                @media screen and (max-width: 1015px) {
                  .pictures {
                    width: 40%;
                    margin: 0 auto;
                  }
                }

                @media screen and (max-width: 800px) {
                  p {
                    font-size: 20px;
                  }
                  .title {
                    width: 90%;
                  }
                }

                @media screen and (max-width: 600px) {
                  p {
                    font-size: 16px;
                  }
                }
              `}</style>
            </>
          );
        });
      }
    });

    return (
      <Layout>
        <div className='wrapper'>
          <div className='title'>
            <img src={photos} alt='Photographs of Good enough live karaoke' />
          </div>
          <div className='flexIt'>
            {this.state.media.length === 0 ? (
              <ColorCircularProgress size={100} thickness={5} />
            ) : (
              pictures
            )}
          </div>
          <div className='title video'>
            <img src={video} alt='Videos of good enough live karaoke' />
          </div>
          <div className='flexIt'>
            {' '}
            {this.state.media.length === 0 ? (
              <ColorCircularProgress size={100} thickness={5} />
            ) : (
              videos
            )}
          </div>
          <style jsx>{`
            @import url('https://fonts.googleapis.com/css?family=Roboto|Roboto+Condensed&display=swap');
            .flexIt {
              border: 10px solid #f1f1f1;
              border-radius: 25px;
              display: flex;
              width: 70%;
              flex-wrap: wrap;
              justify-content: center;
              margin: 0 auto;
              padding: 50px;
              min-height: 55vh;
              max-width: 1400px;
              margin-bottom: 50px;
            }

            .title {
              width: 80%;
              margin: 0 auto;
              margin-bottom: 50px;
            }

            .video {
              margin-top: 100px;
            }

            .wrapper {
              margin-top: 18vh;
            }

            .clientTitle {
              margin-bottom: 50px;
            }
            .clientTitleTest {
              margin-top: 150px;
              margin-bottom: 80px;
            }

            @media screen and (max-width: 800px) {
              p {
                font-size: 20px;
              }
              .title {
                width: 90%;
              }
            }

            @media screen and (max-width: 600px) {
              p {
                font-size: 16px;
              }
            }
          `}</style>
        </div>
      </Layout>
    );
  }
}
