        //         </div>
        //         <style jsx>{`
        //           p {
        //             font-family: 'Roboto', sans-serif;
        //             font-size: 24px;
        //             color: #f1f1f1;
        //             width: 80%;
        //             margin: 25px auto;
        //           }
        //         `}</style>
        //       </>
        //     );
        //   },
        // );
      }
    });

    return (
      <Layout>
        <div className='wrapper'>
          <h2 className='clientTitle'>CLIENTS</h2>
          <ClientImages />
          <h2 className='contactNeon'>TESTIMONIALS</h2>
          {testimonials}
          <style jsx>{`
            @import url('https://fonts.googleapis.com/css?family=Roboto|Roboto+Condensed&display=swap');
            h1,
            h2 {
              white-space: nowrap;
              margin: 0 -5px 0 0;
              letter-spacing: 5px;
              line-height: 1;
              font-size: 7vw;
              font-family: 'Roboto', sans-serif;
              font-weight: 100;
              text-align: center;
              color: #fee;
              text-shadow: 0 -40px 100px, 0 0 2px, 0 0 1em blue, 0 0 0.5em blue,
                0 0 0.1em blue, 0 10px 3px #000;
            }

            h1 {
              margin-bottom: 50px;
            }

            .wrapper {
              margin-top: 20vh;
            }

            .clientTitle {
              margin-bottom: 50px;
            }
          `}</style>
        </div>
      </Layout>
    );
  }
}
