import Layout from '../components/MyLayout';
const contentful = require('contentful');
import ClientImages from '../components/ClientImages';
import TestHeader from '../components/TestHeader';

export default class About extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      testimonials: [],
    };
  }

  componentDidMount() {
    const client = contentful.createClient({
      space: 'sqmp3jmwaedr',
      accessToken: '01TsFxZR2mrw_VWsuCtzZCBCzKsrvCTDX9is-6UPzqU',
    });
    client.getEntries({ content_type: 'about' }).then(response => {
      this.setState({
        testimonials: response.items,
      });
    });
  }

  render() {
    const testimonials = this.state.testimonials.map((testimonials, i) => {
      if (this.state.testimonials[0]) {
        return testimonials.fields.largeTestimonials.content[0].content.map(
          (testimonials, i) => {
            return (
              <>
                <div>
                  <p>{testimonials.content[0].content[i].value}</p>
                  {/* <p>{testimonials.content[0].content[1].value}</p> */}
                </div>
                <style jsx>{`
                  p {
                    font-family: 'Roboto', sans-serif;
                    font-size: 24px;
                    color: #f1f1f1;
                    width: 80%;
                    margin: 25px auto;
                  }
                `}</style>
              </>
            );
          },
        );
      }
    });

    return (
      <Layout>
        <div className='wrapper'>
          <h2 className='clientTitle'>CLIENTS</h2>
          <ClientImages />
          <h2 className='contactNeon'>TESTIMONIALS</h2>
          {testimonials}
          <style jsx>{`
            @import url('https://fonts.googleapis.com/css?family=Roboto|Roboto+Condensed&display=swap');
            h1,
            h2 {
              white-space: nowrap;
              margin: 0 -5px 0 0;
              letter-spacing: 5px;
              line-height: 1;
              font-size: 7vw;
              font-family: 'Roboto', sans-serif;
              font-weight: 100;
              text-align: center;
              color: #fee;
              text-shadow: 0 -40px 100px, 0 0 2px, 0 0 1em blue, 0 0 0.5em blue,
                0 0 0.1em blue, 0 10px 3px #000;
            }

            h1 {
              margin-bottom: 50px;
            }

            .wrapper {
              margin-top: 20vh;
            }

            .clientTitle {
              margin-bottom: 50px;
            }
          `}</style>
        </div>
      </Layout>
    );
  }
}
