import React from 'react';
import ReactDOM from 'react-dom';
const contentful = require('contentful');

import Slider from 'react-slick';
import '../index.css';

class ClientCarousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clients: [],
    };
  }
  componentDidMount() {
    const client = contentful.createClient({
      space: 'sqmp3jmwaedr',
      accessToken: '01TsFxZR2mrw_VWsuCtzZCBCzKsrvCTDX9is-6UPzqU',
    });

    client.getEntries({ content_type: 'clients' }).then(response => {
      this.setState({
        clients: response.items,
      });
    });
  }

  render() {
    return (
      <div className='container'>
        <h2>Clients</h2>

        <div className='flexContainer'>
          {this.state.clients[0]
            ? this.state.clients[0].fields.clientImages.map((images, i) => (
                <div className='clientImages' key={i}>
                  <img src={images.fields.file.url} />
                </div>
              ))
            : ''}
        </div>

        <style jsx>{`
          .clientImages img {
            width: 100px;
            height: 50px;
            border: none;
            border-radius: 5px;
          }

          .clientImages {
            display: flex;
            width: 100px;
            flex-direction: row;
          }

          .flexContainer {
            display: flex;
          }
        `}</style>
      </div>
    );
  }
}

export default ClientCarousel;
