import Link from 'next/link';

const linkStyle = {
  marginRight: 15,
};

const Header = () => (
  <div>
    <nav class='navBar'>
      <nav class='wrapper'>
        <div class='logo'></div>
        <input type='checkbox' id='menu-toggle' />
        <label for='menu-toggle' class='label-toggle'></label>
        <ul>
          <li>
            <a href='#'>Lorem</a>
          </li>
          <li>
            <a href='#'>Ipsum</a>
          </li>
          <li>
            <a href='#'>Serum</a>
          </li>
        </ul>
      </nav>
    </nav>
    <header class='wrapper'>
      <h1>Big ol' Lorem</h1>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Debitis, sint
        corporis dolores omnis consectetur quaerat nesciunt beatae maiores,
        itaque labore ex vero pariatur nulla non fugiat, facilis eaque sunt
        doloremque!
      </p>
    </header>
    {/* <Link href='/'>
      <a style={linkStyle}>Home</a>
    </Link>
    <Link href='/about'>
      <a style={linkStyle}>About</a>
    </Link> */}
    <style jsx>{`
      body {
        background-color: $color1;
        color: $color2;
        font-family: 'Oswald', sans-serif;
        line-height: 2em;
        text-transform: uppercase;
      }

      h1 {
        font-size: 3em;
        padding: 3em 0 1em 0;
      }

      .navBar {
        background-color: $color2;
        position: fixed;
        min-width: 100%;
      }

      .logo {
        background-color: $color1;
        border-radius: 50px;
        display: inline-block;
        height: 45px;
        margin: 1em 0;
        width: 45px;
      }


      .wrapper {
        margin: 0 auto;
        max-width: 960px;
        padding: 0 2%;
      }



        li {
          display: inline-block;
        }

        #menu-toggle {
          display: none;
        }

        .label-toggle {
          display: none;
        }

        .wrapper {
          align-items: center;
          display: flex;
          justify-content: space-between;
        }

        @media screen and (max-width: 768px) {

          ul {
            background-color: $color1;
            display: block;
            height: 0;
            list-style-type: none;
            opacity: 0;
            text-align: center;
            transition: all 1s ease;
            width: 100%;
            visibility: hidden;
          }

          li {
            border-bottom: 2px solid $color2;
            color: $color2;
            display: block;
            font-size: 1.5em;
            padding: 2em 0;
          }

          #menu-toggle:checked ~ ul {
            opacity: 1;
            height: 100vh;
            visibility: visible;
          }



          .wrapper {
            display: block;
          }
        }
      }
    `}</style>
  </div>
);

export default Header;
