import React from 'react';
import ReactDOM from 'react-dom';
const contentful = require('contentful');

import Slider from 'react-slick';
import '../index.css';

class ClientCarousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clients: [],
    };
  }
  componentDidMount() {
    const client = contentful.createClient({
      space: 'sqmp3jmwaedr',
      accessToken: '01TsFxZR2mrw_VWsuCtzZCBCzKsrvCTDX9is-6UPzqU',
    });

    client.getEntries({ content_type: 'clients' }).then(response => {
      this.setState({
        clients: response.items,
      });
    });
  }

  sliders() {
    return this.state.clients[0].fields.clientImages.map(data => {
      return (
        <div key={data}>
          <img alt='image' src={data} />
        </div>
      );
    });
  }
  render() {
    var settings = {
      dots: true,
      autoplay: true,
      autoplaySpeed: 3000,
    };
    return (
      <div className='container'>
        <Slider {...settings}>
          {/* <div> */}
          {this.state.clients[0]
            ? this.state.clients[0].fields.clientImages.map((images, i) => (
                <div key={i}>
                  <img src={images.fields.file.url} />
                </div>
              ))
            : ''}
          {/* </div> */}
        </Slider>
      </div>
    );
  }
}

export default ClientCarousel;
