import Layout from '../components/MyLayout';
const contentful = require('contentful');
import TestHeader from '../components/TestHeader';
import Link from 'next/link';
import CircularProgress from '@material-ui/core/CircularProgress';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import img from '../assets/upcomingShowsHead.svg';

const ColorCircularProgress = withStyles({
  root: {
    color: '#f1f1f1',
  },
})(CircularProgress);

export default class Shows extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      shows: [],
    };
  }

  componentDidMount() {
    const client = contentful.createClient({
      space: 'sqmp3jmwaedr',
      accessToken: '01TsFxZR2mrw_VWsuCtzZCBCzKsrvCTDX9is-6UPzqU',
    });
    client.getEntries({ content_type: 'upcomingShows' }).then(response => {
      this.setState({
        shows: response.items,
      });
    });
  }
  render() {
    const shows = this.state.shows.map((shows, i) => {
      if (shows.fields.showOne) {
        const actualShows = Object.values(shows.fields);
        return actualShows.map((show, i) => {
          if (show.content) {
            const link = show.content[0].content[1].data.uri;

            return (
              <>
                <p>
                  <a target='_blank' href={link}>
                    {show.content[1].content[0].value}
                  </a>
                </p>
                <style jsx>{`
                  a {
                    text-decoration: none;
                    color: #f1f1f1;
                    transition: all 0.4s;
                  }
                  p {
                    text-align: center;
                    font-size: 18px;
                  }

                  a:hover {
                    text-shadow: 0 -40px 100px, 0 0 2px, 0 0 1em #761f6b,
                      0 0 0.5em #761f6b, 0 0 0.1em #761f6b, 0 10px 3px #000;
                  }
                `}</style>
              </>
            );
          }
        });
      }
    });

    return (
      <Layout>
        <div className='listWrapper'>
          <div className='title'>
            <h3>UPCOMING SHOWS</h3>
          </div>
          <h4>Click a show for more info</h4>
          <div className='shows'>
            {this.state.shows.length === 0 ? (
              <ColorCircularProgress size={100} thickness={5} />
            ) : (
              shows
            )}
          </div>
        </div>
        <style jsx>{`
          h3 {
            font-family: 'Concert One', sans-serif;
            white-space: nowrap;
            margin: 0 -5px 0 0;
            letter-spacing: 5px;
            line-height: 1;
            font-size: 7vw;
            font-weight: 100;
            text-align: center;
            color: #fee;
            text-shadow: 0 -40px 100px, 0 0 2px, 0 0 1em #761f6b,
              0 0 0.5em #761f6b, 0 0 0.1em #761f6b, 0 10px 3px #000;
          }
          h4 {
            font-family: 'Montserrat', sans-serif;
            font-weight: 100;
            text-align: center;
            font-size: 20px;
            color: #f1f1f1;
            margin: 50px 0;
          }

          .listWrapper {
            margin-top: 18vh;
          }

          .title {
            width: 80%;
            margin: 0 auto;
          }

          .shows {
            font-family: 'Montserrat', sans-serif;
            margin: 0 auto;
            text-align: left;
            font-size: 28px;
            color: #f1f1f1;
            min-height: 55vh;
            width: 70%;
            line-height: 38px;
          }

          p {
            color: #f1f1f1;
            font-size: 18px;
            display: flex;
            justify-content: center;
          }

          a:hover {
            text-decoration: underline;
          }

          @media screen and (max-width: 900px) {
            .shows {
              font-size: 16px;
            }
          }

          @media screen and (max-width: 650px) {
            .title {
              width: 90%;
            }

            a {
              font-size: 14px;
            }

            p {
              font-size: 16px;
            }
            h4 {
              font-size: 16px;
            }
            .shows {
              font-size: 14px;
              width: 85%;
              line-height: 22px;
            }
          }
        `}</style>
      </Layout>
    );
  }
}
